CREATE USER test
  IDENTIFIED BY password
  DEFAULT TABLESPACE SYSTEM
  QUOTA 20M on SYSTEM;


GRANT create session TO test;
GRANT create table TO test;
GRANT create view TO test;
GRANT create any trigger TO test;
GRANT create any procedure TO test;
GRANT create sequence TO test;
GRANT create synonym TO test;

CREATE TABLE test.customers (
    CUSTOMERID   NUMBER(10,0) NOT NULL,
    CUSTOMERNAME VARCHAR2(50 BYTE),
    CONTACTNAME  VARCHAR2(50 BYTE),
    ADDRESS      VARCHAR2(50 BYTE),
    CITY         VARCHAR2(50 BYTE),
    POSTALCODE   VARCHAR2(50 BYTE),
    COUNTRY      VARCHAR2(50 BYTE) 
);



CREATE TABLE test.orders (
OrderID NUMBER(10,0) NOT NULL,
CustomerID NUMBER(10,0) NOT NULL,
EmployeeID NUMBER(10,0) NOT NULL,
OrderDate VARCHAR2(50 BYTE),
ShipperID NUMBER(10,0));



CREATE PUBLIC SYNONYM customers FOR test.customers;
CREATE PUBLIC SYNONYM orders FOR test.orders;


